styles.css:
/* -------------------- */
/* FONTS */
/* -------------------- */
@import url('https://fonts.googleapis.com/css2?family=Quicksand:wght@300&family=Raleway:wght@300,700&display=swap');
/* -------------------- */
/* RESET */
/* -------------------- */
* {
  font-family: 'Quicksand', sans-serif;
  font-weight: 300;
}
a {
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
}
h1,
h2,
h3 {
  font-family: 'Raleway', sans-serif;
}
h1 a,
h2 a,
h3 a,
.header a {
  border-bottom: 3px solid #62BFD1;
}
h1 a:hover,
h2 a:hover,
h3 a:hover,
.header a:hover {
  border-bottom: none;
  border-bottom: 3px solid #FFF;
  text-decoration: none;
}
h2 {
  font-size: 48px;
  font-weight: 700;
  line-height: 54px;
}
h3 {
  font-size: 34px;
  font-weight: 200;
  line-height: 40px;
}
h4 {
  font-size: 14px;
  opacity: .5;
}
p {
  font-size: 18px;
  font-weight: 200;
  line-height: 32px;
}
/* -------------------- */
/* FEATURE SHOWCASE */
/* -------------------- */
body .feature-showcase .is-layout-flex {
  gap: 48px;
}
.feature-showcase {
 margin-bottom: 84px;
}
/* -------------------- */
/* FOOTER */
/* -------------------- */
footer h2.has-medium-font-size {
  font-size: 36px !important;
}
footer .powered-by {
  font-size: 12px;
  text-transform: uppercase;
}
/* -------------------- */
/* HEADER */
/* -------------------- */
.header p {
  font-family: 'Raleway', sans-serif;
  font-size: 34px;
  font-weight: 300;
  line-height: 52px;
}
.header p a {
  border-bottom-width: 3px;
}
.header p em {
  background: #D0F7D1;
  font-style: normal;
  position: relative;
}
/* -------------------- */
/* POST */
/* -------------------- */
.post-meta a {
  font-size: 12px;
  text-transform: uppercase;
}
/* -------------------- */
/* RESPONSIVE */
/* -------------------- */
@media only screen and (max-width: 600px) {
  body .feature-showcase .is-layout-flex {
    gap: 24px;
  }
  .feature-showcase {
    padding: 0 40px;
  }
  .header-single {
    padding: 0 40px;
  }
  footer .wp-block-group {
    padding: 0 40px;
  }
  .header {
    padding: 40px !important;
  }
  .home-feature-right .wp-block-columns {
    flex-direction: column-reverse;
  }
  .wp-block-query {
    padding: 0 40px;
  }
  .wp-block-query h2 {
    font-size: 36px;
    line-height: 44px;
  }
}
